import React from 'react'
import useStyles from './styles';
import {Card, CardActions, CardContent, CardMedia, Button, Typography} from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import Moment from 'react-moment';
import {useDispatch} from 'react-redux';
import {deletePost, getPosts, likePost} from '../../../actions/posts';

export function Post({post, setCurrentId}) {

  const classes = useStyles();
  const dispatch = useDispatch();

  return (
    <Card className={classes.card}>
      <CardMedia className={classes.media} image={post.selectedFile} title={post.title}>
      </CardMedia>
      <div className={classes.overlay}>
        <Typography variant='h6'>{post.creator}</Typography>
        <Typography variant='body2'>
          <Moment fromNow>
            {post.createdAt}
          </Moment>
        </Typography>
      </div>
      <div className={classes.overlay2}>
        <Button style={{color:'white'}} size='small' onClick={() => setCurrentId(post._id)}>
          <MoreHorizIcon fontSize='default'></MoreHorizIcon>
        </Button>
      </div>
      <div className={classes.details}>
        <Typography variant='body2' color='textSecondary'>{post.tags.map((tag) => `#${tag} `)}</Typography>
      </div>
      <Typography variant='h5' className={classes.title} gutterBottom>{post.title}</Typography>
      <CardContent>
        <Typography variant='h5' gutterBottom>{post.message}</Typography>
      </CardContent>
      <CardActions className={classes.cardActions}>
        <Button size='small' color='primary' onClick={() => {dispatch(likePost(post._id)); dispatch(getPosts)}}>
          <ThumbUpAltIcon fontSize='small'></ThumbUpAltIcon>
          Like
          {post.likeCount}
        </Button>
        <Button size='small' color='primary' onClick={() => {dispatch(deletePost(post._id))}}>
          <DeleteIcon fontSize='small'></DeleteIcon>
          Delete
        </Button>
      </CardActions>
    </Card>
  )
}
