import React from 'react';
import { TextField, Grid, InputAdorment, IconButton } from '@material-ui/core';

import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';

function Input({name, handleChange, label, half, autoFocus, type, handleShowPassword}) {
  return (
    <Grid item xs={12} sm={half ? 6: 12} >
        <TextField
            name={name}
            onChange={handleChange}
            variant='outlined'
            required
            fullWidth
            label={label}
            autoFocus={autoFocus}
            type={type}
            InputProps={name === 'Password' && {
                endAdornment : (
                    <InputAdorment position='end'>
                        <IconButton onClick={handleShowPassword}>
                            {type === 'Password' ? <Visibility></Visibility> : <VisibilityOff></VisibilityOff>}
                        </IconButton>
                    </InputAdorment>
                )
            }}
        ></TextField>
    </Grid>
  )
}

export default Input
