export const postReducer = (posts = [], action) => {
    switch(action.type)
    {
        case 'FETCH-ALL':
            console.log(action.payload);
            return action.payload;
        case 'CREATE':
            return [...posts, action.payload];
        default:
            return posts;
    }
};
